import logo from './logo.svg';
import './App.css';

import React, { useEffect, Suspense } from 'react';

import { BrowserRouter, Redirect, Switch, Route } from 'react-router-dom';

import { connect } from 'react-redux';

import Logout from '../src/pages/Auth/Logout/Logout';

import LandingPage from '../src/pages/Landing-Page/Landing-Page';

import Shop from '../src/pages/Shop/Shop';

import Auth from '../src/pages/Auth/Auth';

import Orders from '../src/pages/Orders';

import Layout from '../src/hoc/Layout/Layout';

import * as actionTypes from '../src/store/actions/index';

import Spinner from './components/UI/Spinner/Spinner';

function App(props) {
  useEffect(() => {
    props.onTryAutoSignIn();
  }, []);

  let routes = (
    <Switch>
      <Route
        path="/auth"
        render={() => {
          return <Auth />;
        }}
      />
      <Route path="/shop" component={Shop} />
      <Route path="/" exact component={LandingPage} />
      <Redirect to="/" />
    </Switch>
  );

  if (props.token) {
    routes = (
      <Switch>
        <Route />
        <Route
          path="/checkout"
          render={(props) => {
            return <Checkout {...props} />;
          }}
        />
        <Route
          path="/orders"
          render={(props) => {
            return <Orders {...props} />;
          }}
        />
        <Route
          path="/auth"
          render={(props) => {
            return <Auth {...props} />;
          }}
        />
        <Route path="/logout" component={Logout} />
        <Route path="/shop" component={Shop} />
        <Route path="/" exact component={LandingPage} />
        <Redirect to="/" />
      </Switch>
    );
  }

  return (
    <BrowserRouter>
      <div>
        <Layout>
          <Suspense fallback={Spinner}>{routes}</Suspense>
        </Layout>
      </div>
    </BrowserRouter>
  );
}

const mapStateToProps = (state) => {
  return {
    token: state.auth.token !== null,
  };
};

const mapDispatchToProps = (state) => {
  return {
    onTryAutoSignIn: (token) =>
      dispatchEvent(actionTypes.authCheckState(token)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
