import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';

import './Pagination.css';

const Pagination = (props) => {
  //   0: {0: {…}, 1: {…}, 2: {…}, id: 'products', value: Array(3)}
  // 1: {id: 'currentPage', value: 1}
  // 2: {id: 'hasNextPage', value: true}
  // 3: {id: 'hasPreviousPage', value: false}
  // 4: {id: 'previousPageNumber', value: 0}
  // 5: {id: 'lastPage', value: 3}

  let pagination = null;

  if (!props.loading && props.products.length !== 0) {
    pagination = (
      <section className="pagination">
        {/* <NavLink to="/">1</NavLink>
      <NavLink to="/">2</NavLink>
      <NavLink to="/">3</NavLink> */}

        {props.products[1].value >= 1 && (
          // <button className="dummy" type="button" onClick={props.on}>
          //   1
          // </button>
          <NavLink to={'/'}>1</NavLink>
        )}

        {props.products[3].value && (
          // <button className="dummy" type="button" onClick={props.onPrevious}>
          //   {props.products[1].value - 1}
          // </button>
          <NavLink>
          {props.products[1].value - 1}
        </NavLink>
        )}

        {props.products[1].value !== 1 &&
          props.products[1].value !== props.products[5].value && (
            <button className="dummy-disabled" type="button" onClick={props.on}>
              {props.products[1].value}
            </button>
          )}

        {props.products[2].value &&
          props.products[1].value !== props.products[5].value && (
            <button className="dummy" type="button" onClick={props.onNext}>
              {props.products[1].value + 1}
            </button>
          )}

        {props.products[1].value !== props.products[5].value && (
          <button className="dummy" type="button" onClick={props.on}>
            {props.products[5].value}
          </button>
        )}
      </section>
    );
  }

  return pagination;
};

const mapStateToProps = (state) => {
  return {
    products: state.shop.products,
    loading: state.shop.loading,
  };
};

const _mapDispatchToProps = () => {
  return {};
};

export default connect(mapStateToProps, _mapDispatchToProps)(Pagination);
