import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

import { combineReducers, configureStore } from '@reduxjs/toolkit'; ///must-have code

// import { compose } from 'redux'; ///optional code (to be removed, dev-only)
// import thunk from 'redux-thunk'; ///maybe must-have code -- foi integrado automaticamente ao REACT-REDUX. --_> agora existe no method 'configureStore'

import authReducer from './store/reducers/authReducer';
import orderReducer from './store/reducers/orderReducer';
import cartReducer from './store/reducers/cartReducer';
import shopReducer from './store/reducers/shopReducer';
import { Provider } from 'react-redux';

import 'bootstrap/dist/css/bootstrap.min.css';

// const composeEnhancers = ///ESSE CÓDIGO AGORA EXISTE DENTRO DO REACT-REDUX
//   process.env.NODE_ENV === 'development'
//     ? window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__
//     : null || compose; ///versão óptima de ATIVAÇÃO DO 'REDUX DEV TOOLS'... --> por meio deste código, o redux devtools só vai funcionar/realmente mostrar o 'redux state' nas redux devtools SE NÓS REALMENTE ESTIVERMOS DURANTE 'dev mode', e não PRODUCTION MODE....

const reducer = combineReducers({
  auth: authReducer,
  order: orderReducer,
  cart: cartReducer,
  shop: shopReducer,
});

const store = configureStore({
  reducer,
  devTools: process.env.NODE_ENV !== 'production',
  middleware: (getDefaultMiddleware) => getDefaultMiddleware(),
});

const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <Provider store={store}>
    <App />
  </Provider>
);
