import React, { useEffect } from 'react';

import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import CartItem from '../../components/Cart/CartItemList/CartItem/CartItem';
import CartItemList from '../../components/Cart/CartItemList/CartItemList';
import NoProduct from '../../components/Cart/NoProduct/NoProduct';
import Spinner from '../../components/UI/Spinner/Spinner';
import Wrapper from '../../components/Wrapper/Wrapper';

import * as cartActionTypes from '../../store/actions/cart';

import Cart from '../../components/Cart/Cart';

const CartPage = (props) => {
  useEffect(() => {
    props.onFetchCart(props.token, props.userId);

    console.log(props.cart.products);
  }, []);

  let cart = (
    <Wrapper>
      <CartItemList>
        <NoProduct>
          <li class="no-product cart-item">
            <h2>There are no products in your Cart.</h2>
          </li>
        </NoProduct>
      </CartItemList>
    </Wrapper>
  );

  if (props.loading) {
    cart = <Spinner></Spinner>;
  }

  console.log(props.loading, props.cart.products);

  if (!props.loading && props.cart.products !== []) {
    cart = (
      <React.Fragment>
        <CartItemList>
          {props.cart.products.map((product) => {
            return (
              <React.Fragment>
                <CartItem
                  productId={product.productId}
                  quantity={product.quantity}
                ></CartItem>
                <form action="/cart/delete-cart-item" onSubmit={props.onSubmit}>
                  <Button
                    buttonType="submit"
                    clicked={'dummy'}
                    buttonClass="btn danger"
                    isEnabled
                    buttonId="button"
                  >
                    Remove from Cart
                  </Button>
                </form>
              </React.Fragment>
            );
          })}
        </CartItemList>
        <NavLink to="/checkout">Order Now!</NavLink>
      </React.Fragment>
    );
  }

  return <Cart>{cart}</Cart>;
};

const mapStateToProps = (state) => {
  return {
    cart: state.cart,
    loading: state.cart.loading,
    totalPrice: state.cart.totalPrice,
    error: state.cart.error,
    token: state.auth.token,
    userId: state.auth.userId,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchCart: (token, userId) => {
      dispatch(cartActionTypes.asyncFetchCartStart(token, userId));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CartPage);
