import React, { useEffect } from 'react';
import { propTypes } from 'react-bootstrap/esm/Image';
import { connect } from 'react-redux';
import CartItemList from '../../components/Cart/CartItemList/CartItemList';
import NoProduct from '../../components/Cart/NoProduct/NoProduct';
import OrderBoard from '../../components/OrderBoard/OrderBoard';
import OrdersItem from '../../components/OrderBoard/Orders/OrdersItem/OrdersItem';
import Wrapper from '../../components/Wrapper/Wrapper';

import * as orderActionTypes from '../../store/actions/order';

const Orders = (props) => {
  useEffect(() => {
    // const token = localStorage.getItem('token');
    // const userId = localStorage.getItem('userId');
    propTypes.onFetchOrders();
  }, []);

  let orders = (
    <Wrapper>
      <CartItemList>
        <NoProduct>
          <li class="no-product cart-item">
            <h2 style={{ fontFamily: 'Lato' }}>
              You haven't placed any orders yet.
            </h2>
          </li>
        </NoProduct>
      </CartItemList>
    </Wrapper>
  );

  if (props.orders !== []) {
    console.log(props.orders);
    orders = (
      <Orders>
        {props.orders.map((order) => {
          return (
            <OrdersItem
              orderId={order.id}
              orderProducts={order.products}
            ></OrdersItem>
          );
        })}
      </Orders>
    );
  }

  return <OrderBoard>{orders}</OrderBoard>;
};

const mapStateToProps = (state) => {
  return {
    orders: state.orders.orders,
    loading: state.orders.loading,
    token: state.auth.token,
    userId: state.auth.userId,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchOrders: () => {
      dispatch(orderActionTypes.asyncFetchOrdersStart());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Orders);
