import React from 'react';
import { NavLink } from 'react-router-dom';

import './Pagination.css';

const Pagination = (props) => {
  return (
    <section className="pagination">
      {/* <NavLink to="/">1</NavLink>
      <NavLink to="/">2</NavLink>
      <NavLink to="/">3</NavLink> */}


{
  props.currentPage > 1 &&
  (
  <button className="dummy" type="button" onClick={props.on}>
        1
  </button>
  )
}


{
(props.hasPreviousPage ) &&
(
  <button className="dummy" type="button" onClick={props.onPrevious}>
    {props.currentPage - 1}
  </button>

)
}


 {
  (props.currentPage !== 1 && props.currentPage !== props.lastPage) &&

  (
    <button className="dummy-disabled" type="button" onClick={props.on}>
      {props.currentPage}
    </button>

  )
 }



{

(props.hasNextPage && props.currentPage !== props.lastPage ) &&

(
  <button className="dummy" type="button" onClick={props.onNext}>
    {props.currentPage + 1}
  </button>

)
}



{
  (props.currentPage !== props.lastPage) &&

  (
    <button className="dummy" type="button" onClick={props.on}>
      {props.lastPage}
    </button>

  )
 }


    </section>
  );
};

export default Pagination;
