import axiosOrder from '../../../../axios-orders';
import React from 'react';
import Button from '../../../UI/Button/Button';

import './OrdersItem.css';

const OrdersItem = (props) => {
  const openPdfInvoice = () => {
    axiosOrder
      .post(
        `/orders/${props.orderId}`,


        {},

        {

          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${props.token}`,
            UserId: `${props.userId}`,
          },
        }
      )
      .then((res) => {
        console.log(res);
        if (res.status === 200) {
            console.log(res);
            console.log(props.orderId);
            const file = new File([res.data], props.orderId, { type: 'application/pdf' });
            const fileUrl = URL.createObjectURL(file);
            console.log(fileUrl);
            // window.open(`blob:/orders/${props.orderId}`);
            window.open(fileUrl);
        }
      });
  };

  return (
    <li className="orders__item" key={props.orderKey}>
      <div className="orders__item-lines">
        <h1 className="order__number">Order #{props.orderId}</h1>
        <ul className="orders__products">
          {props.orderProducts.map((product) => {
            return (
              <li key={product._id}>
                <div className="orders__products-item">
                  <span>Product: </span>
                  {product.product.title} ({product.quantity})
                  <p>
                    <span>Price:</span> ${product.product.price}
                  </p>
                </div>
              </li>
            );
          })}
          <li className="order__price">
            <h2>Total Price: US$ {props.orderPrice.toFixed(2)}</h2>
            <p>
              {/* <a className="order__link" href={`/orders/${props.orderId}`}>
                Invoice
              </a> */}
              <Button
                clicked={() => {
                  openPdfInvoice();
                }}
                isEnabled
              >
                Invoice
              </Button>
            </p>
          </li>
        </ul>
      </div>
    </li>
  );
};

export default OrdersItem;
