import React, { useState } from 'react';
import Grid from '../../components/Grid/Grid';
import ProductItem from '../../components/ProductItem/ProductItem';

const ProductEditor = (props) => {
  const [controls, setControls] = useState({
    title: {
      inputType: 'input',
      elementConfig: {
        type: 'text',
        placeholder: 'Product Title',
      },
      value: '',
      validation: {
        required: true,
      },
      valid: false,
      touched: false,
    },
    price: {
      inputType: 'input',
      elementConfig: {
        type: 'number',
        placeholder: 'Product Price',
      },
      value: '',
      validation: {
        required: true,
        isNumber: true,
      },
      valid: false,
      touched: false,
    },
    description: {
      inputType: 'textarea',
      elementConfig: {
        type: 'text',
        placeholder: 'Your Description',
      },
      value: '',
      validation: {
        required: true,
        rows: 5,
      },
      valid: false,
      touched: false,
    },
  });

  return (
    <Grid isEdit={true}>
      <ProductItem isEdit={'add'}></ProductItem>
    </Grid>
  );
};

export default ProductEditor;
