import React, { useEffect } from 'react';


import Spinner from '../UI/Spinner/Spinner';

import * as shopActionTypes from '../../store/actions/shop';

import ProductItem from '../ProductItem/ProductItem';

import './ProductList.css';
import { connect } from 'react-redux';

const ProductList = (props) => {
  useEffect(() => {
    props.onFetchProducts();
  }, []);

  let productList = (<div>
    
    <Spinner/>
    <p className="loading-text">Loading<span></span></p>
    
    </div>)

  if (!props.loading) {
    productList = Object.entries(props.products).map((product, index) => {

      if (index === 0) {
        return Object.entries(product[1]).map((innerProduct) => {
          console.log(innerProduct[3])
          if (innerProduct[1] !== 'products') {
            return (
              <ProductItem
              key={innerProduct[1].title}
                title={innerProduct[1].title}
                price={innerProduct[1].price}
                description={innerProduct[1].description}
                imageUrl={innerProduct[1].imageUrl}
              ></ProductItem>
            );
          }
        });
      }
    });
  }

  return <div className="product__list">{productList}</div>;

};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchProducts: () => {
      dispatch(shopActionTypes.asyncFetchProductsStart());
    },
  };
};

const mapStateToProps = (state) => {
  return {
    products: state.shop.products,
    loading: state.shop.loading,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
