import React from 'react';
import NavigationItem from '../NavigationItems/NavigationItem/NavigationItem';

import './NavigationItems.css';

const navItems = [
  { id: 'add-product', text: 'Add Product', link: '/add-product'},
  {
    id: 'admin-products',
    text: 'Admin Products',
    link: '/admin-products',
  },
  { id: 'cart', text: 'Cart', link: '/cart'},
  { id: 'orders', text: 'Orders', link: '/orders'},
];

const navigationItems = (props) => {
  let authenticate = (
    <NavigationItem link={'/auth'} closed={props.closed} text="Get Started">
    </NavigationItem>
  );

  if (props.logout) {
    authenticate = (
      <NavigationItem link={'/logout'} closed={props.closed} text="Logout">
      </NavigationItem>
    );
  }

  return (
    <ul className={
      props.type === "main" ? "main-header__item-list" : "mobile-nav__item-list"
    }>
      <NavigationItem link={'/'} text="Shop" type={props.type === "main" ? "main-header__item" : "mobile-nav__item"}>Shop</NavigationItem>
      {props.logout &&
        navItems.map((item) => {
          return (
            <NavigationItem key={item.id} link={item.link} type={ 
              props.type
            }
            
            text={item.text}
            >
            </NavigationItem>
          );
        })}
      <NavigationItem link={'/shop'} text="Products" type={props.type === "main" ? "main-header__item" : "mobile-nav__item"}>Products</NavigationItem>
      {authenticate}
    </ul>
  );
};

export default navigationItems;
