import React, { useEffect, useState } from 'react';

import Button from '../../components/UI/Button/Button';
import { NavLink } from 'react-router-dom';
import Input from '../../components/UI/Input/Input';

import '../Cart/Cart.css';
import './Auth.css';
import { is } from 'immer/dist/internal';

const Auth = (props) => {
  let errorMessage = false;

  const [isSignup, setIsSignup] = useState(false);

  const authSwitchHandler = () => {
    setIsSignup(!isSignup);
  };

  if (props.errorMessage) {
    errorMessage = props.errorMessage;
  }

  return (
    <React.Fragment>
      <div className="cart">
        <div className="wrapper">
          <ul className="cart__item-list">
            <div
              className="no-product"
              style={{ backgroundColor: 'var(--lightest-green)' }}
            >
              <li className="no-product cart-item">
                <div className="user-message user-message--error">
                  {errorMessage}
                </div>

                <form className="login-form" action="/login" method="POST">
                  <Input
                    inputType={'hidden'}
                    inputName={'_csrf'}
                    inputId="csrf"
                    inputPlaceholder="csrf"
                    inputClassName="hidden"
                  ></Input>
                  <div className="control">
                    <label htmlFor="email">Email</label>

                    <Input
                      inputPlaceholder="exemplo@exemplo"
                      inputType="email"
                      inputId="email"
                      inputName="email"
                      inputClassName="email"
                    ></Input>
                  </div>
                  <div className="control">
                    <label htmlFor="password">Password</label>
                    <Input
                      inputPlaceholder="exemplo"
                      inputType="password"
                      inputId="password"
                      inputName="password"
                      inputClassName="password"
                    ></Input>
                  </div>
                  <div className="switch-link">
                    {!isSignup ? (
                      <p>
                        No Account? &nbsp;
                        <Button
                          buttonClass="command"
                          clicked={authSwitchHandler}
                          buttonType="button"
                        >
                          {' '}
                          Join NodeShop
                        </Button>
                      </p>
                    ) : (
                      <p>
                        Already have an account? &nbsp;
                        <Button
                          buttonClass="command"
                          clicked={authSwitchHandler}
                          buttonType="button"
                        >
                          Login
                        </Button>
                      </p>
                    )}
                  </div>
                  <Button
                    buttonType="button"
                    buttonClass="button"
                    buttonId="button"
                  >
                    {!isSignup ? 'Login' : 'Signup'}
                  </Button>
                </form>
              </li>
            </div>
          </ul>
        </div>
      </div>

      <div className="dummy-row"></div>
    </React.Fragment>
  );
};

export default Auth;
