import React from 'react';

import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';

import Toolbar from '../../components/Toolbar/Toolbar';

import { useState } from 'react';

import { connect } from 'react-redux';

import Aux from '../Auxiliary/Auxiliary';

import LayoutStyle from './Layout.module.css';

import Footer from '../../components/Footer/Footer';



import Backdrop from '../../components/Backdrop/Backdrop';

const Layout = (props) => {
  const [showSideDrawer, setShowSideDrawer] = useState(false);
  const [showBackdrop, setShowBackdrop] = useState(false);


  const sideDrawerToggleHandler = () => {
    console.log('ENTERED')
    setShowSideDrawer(!showSideDrawer);
    setShowBackdrop(true);
  };



  const backdropClickHandler = () => {
    console.log('ENTERED2 LINE')
    setShowSideDrawer(false);
    setShowBackdrop(false);
  }

  return (<Aux>
      <Backdrop open={showBackdrop} clicked={backdropClickHandler}></Backdrop>
      <Toolbar drawerToggleClicked={sideDrawerToggleHandler} navOpen={showSideDrawer}/>
      <main className={LayoutStyle.Content}>{props.children}</main>
      <Footer />
    </Aux>
  );
};

const mapStateToProps = (state) => {
  return {
    token: state.auth.token !== null,
    email: state.auth.email,
  };
};


export default connect(mapStateToProps)(Layout);



