import React, { useEffect } from 'react';
import NavigationItem from '../NavigationItems/NavigationItem/NavigationItem';

import { NavLink } from 'react-router-dom';
import './NavigationItems.css';
import { connect } from 'react-redux';

const navItems = [
  { id: 'product-editor', text: 'Add Product', link: '/product-editor' },
  {
    id: 'admin-products',
    text: 'Admin Products',
    link: '/admin-shop',
  },
  // { id: 'cart', text: 'Cart', link: '/cart' },
  { id: 'orders', text: 'Orders', link: '/orders' },
];




const NavigationItems = (props) => {



  useEffect(
    () => {
      props.onFetchCart()
    },

    []
  )






  let authenticate = (
    <ul
      className={
        props.type === 'main'
          ? 'main-header__item-list'
          : 'mobile-nav__item-list'
      }
    >
      <li
        className={
          (props.type === 'main' ? 'main-header__item' : 'mobile-nav__item') +
          ' auth'
        }
      >
        <NavLink to="/auth" exact="true" onClick={props.onChooseItem}>
          Get Started
        </NavLink>
      </li>
    </ul>
  );

  if (props.token) {
    authenticate = (
      <ul
        className={
          props.type === 'main'
            ? 'main-header__item-list'
            : 'mobile-nav__item-list'
        }
      >
        <li
          className={
            (props.type === 'main' ? 'main-header__item' : 'mobile-nav__item') +
            ' auth'
          }
        >
          <NavLink to="/cart" exact="true" onClick={props.onChooseItem} style={{'marginRight': '3.25rem'}} className="cart-link">
            <i className="fa fa-shopping-cart fa-2x"></i>
            <div className="cart-link-number">{props.cartItemsNumber}</div>
          </NavLink>
          <NavLink to="/logout" exact="true" onClick={props.onChooseItem}  >
            Logout
          </NavLink>
        </li>
      </ul>
    );
  }
  return (
    <React.Fragment>
      <ul
        className={
          props.type === 'main'
            ? 'main-header__item-list'
            : 'mobile-nav__item-list'
        }
      >
        <NavigationItem
          link={'/'}
          text="Shop"
          type={props.type}
          clicked={props.onChooseItem}
        >
          Shop
        </NavigationItem>
        {props.token &&
          navItems.map((item) => {
            return (
              <NavigationItem
                key={item.id}
                link={item.link}
                type={props.type}
                text={item.text}
                clicked={props.onChooseItem}
              ></NavigationItem>
            );
          })}
        <NavigationItem
          link={`/shop`}
          text="Products"
          type={props.type}
          clicked={props.onChooseItem}
        >
          Products
        </NavigationItem>
      </ul>
      {authenticate}
    </React.Fragment>
  );
};

const mapStateToProps = (state) => {
  return {
    token: state.auth.token,
    userId: state.auth.userId,
    cartItemsNumber: state.cart.products.length
  };
};

export default connect(mapStateToProps)(NavigationItems);
