import React, { useEffect, useState } from 'react';

import Button from '../../components/UI/Button/Button';
import { NavLink } from 'react-router-dom';
import Input from '../../components/UI/Input/Input';

import '../Cart/Cart.css';
import './Auth.css';

const Auth = (props) => {
  let errorMessage = false;

  if (props.errorMessage) {
    errorMessage = props.errorMessage;
  }

  return (
    <div className="cart">
      <div className="wrapper">
        <ul className="cart__item-list">
          <div
            className="no-product"
            style={{ 'background-color': 'var(--lightest-green);' }}
          >
            <li className="no-product cart-item">
              <div className="user-message user-message--error">
                {errorMessage}
              </div>

              <form className="login-form" action="/login" method="POST">
                {/* <input
                  type="hidden"
                  value="<%= csrfToken %>"
                  name="_csrf"
                ></input> */}
                <Input
                  inputType={'hidden'}
                  inputName={'_csrf'}
                  inputId="csrf"
                  inputPlaceholder="csrf"
                  inputClassName="hidden"
                ></Input>
                <div className="control">
                  <label htmlFor="email">Email</label>

                  <Input
                    inputPlaceholder="exemplo@exemplo.com"
                    inputType="email"
                    inputId="email"
                    inputName="email"
                    inputClassName="email"
                  ></Input>
                </div>
                <div className="control">
                  <label htmlFor="password">Password</label>
                  <Input
                    inputPlaceholder="exemplo"
                    inputType="password"
                    inputId="password"
                    inputName="password"
                    inputClassName="password"
                  ></Input>
                </div>
                <div className="reset-password-link">
                  <NavLink to="/">Forgot your password?</NavLink>
                </div>
                <Button>Login</Button>
              </form>
            </li>
          </div>
        </ul>
      </div>
    </div>
  );
};

export default Auth;
