import React from 'react';

import NavigationItem from '../NavigationItems/NavigationItem/NavigationItem';

import NavigationItemsStyle from './NavigationItems.css';

const navItems = [
  { id: 'add-product', text: 'Add Product', link: '/add-product', auth: true },
  {
    id: 'admin-products',
    text: 'Admin Products',
    link: '/admin-products',
    auth: true,
  },
  { id: 'cart', text: 'Cart', link: '/cart', auth: true },
  { id: 'orders', text: 'Orders', link: '/orders', auth: true },
];

const navigationItems = (props) => {
  let authenticate = (
    <NavigationItem link={'/auth'} closed={props.closed}>
      Get Started
    </NavigationItem>
  );

  if (props.logout) {
    authenticate = (
      <NavigationItem link={'/logout'} closed={props.closed}>
        Logout
      </NavigationItem>
    );
  }

  return (
    <ul className={NavigationItemsStyle.NavigationItems}>
      <NavigationItem link={'/'}>Shop</NavigationItem>
      {props.logout &&
        navItems.map((item) => {
          return (
            <NavigationItem key={item.id} link={item.link}>
              {item.text}
            </NavigationItem>
          );
        })}
      <NavigationItem link={'/shop'}>Products</NavigationItem>
      {authenticate}
    </ul>
  );
};

export default navigationItems;
