import React from 'react';

import './Input.css';

const Input = (props) => {
  let inputElement = null;

  const inputClasses = [''];


  if (props.touched && !props.invalid) {
    inputClasses.push('valid');
  }

  if (props.invalid && props.shouldValidate && props.touched) {
    inputClasses.push('invalid');
  }


 

  switch (props.elementType) {
    case 'input':
      inputElement = (
        <input
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
          value={props.value}
          key={props.key}
        />
      );
      break;
    case 'textarea':
      inputElement = (
        <textarea
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
          key={props.key}
        />
      );
      break;
    default:
      inputElement = (
        <input
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
          value={props.value}
        />
      );
  }

  return inputElement;
};

export default Input;
