import React from 'react';

import './Input.css';

const Input = (props) => {
  let inputElement = null;

  const inputClasses = [];

  switch (props.elementType) {
    case 'input':
      inputElement = (
        <input
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
          value={props.value}
        />
      );
      break;
    case 'textarea':
      inputElement = (
        <textarea
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
        />
      );
      break;
    default:
      inputElement = (
        <input
          onChange={props.changed}
          {...props.elementConfig}
          className={inputClasses.join(' ')}
          value={props.value}
        />
      );



      return {inputElement}
  }
};

export default Input;
