import React from 'react';


import './Input.css';

const Input = (props) => {
  return (
    <input
      type={props.inputType}
      name={props.inputName}
      required
      className={
        props.inputClass ===
        ('email' || 'password' || 'confirmPassword' || 'hidden')
          ? props.inputClass
          : 'error'
      }
      placeholder={props.inputPlaceholder}
    />
  );
};

export default Input;
