import React from 'react';
import { NavLink } from 'react-router-dom';

import './ProductBox.css';

import '../../UI/Button/Button.css';
import Input from '../../UI/Input/Input';

const ProductBox = (props) => {



  const loadFile = () => {

  }

  let productBox = (
    <div className="product__box">
    <header className="product__header">
      <h1 className="product__title">{props.title}</h1>
    </header>
    <div className="product__content">
      <h2 className="product__price">
        <sup>US$</sup> {props.price}
      </h2>
      <p className="product__description">
        <strong>Description:</strong> {props.description}
      </p>
      <div className="product__actions">
        <NavLink className="button" to="/">
          {!props.isAdmin ? 'Details' : 'Edit'}
        </NavLink>
        <NavLink className="button" to="/">
          {!props.isAdmin ? 'Add to Cart' : 'Delete'}
        </NavLink>
      </div>
    </div>
  </div>
  )

  if (props.isEdit === 'add') {

    productBox = (
      <div className="product__box">
        <form className="product-form" 
        onSubmit=""
        
        encType="multipart/form-data"
        >
        <div className="control">
          <label htmlFor="image">Image</label>
          <Input changed={() => {loadFile()}} elementType="file"></Input>
        </div>
        <div className="control">
          <label htmlFor="title">Title</label>
          <Input changed={true} elementType="text"
          placeholder="Product Title"
          
          ></Input>
        </div>


        </form>
      </div>

    )
  }

  return productBox;
};

export default ProductBox;
