import * as shopActionTypes from '../actions/shopActionTypes';

import axiosOrder from '../../axios-orders';

export const fetchProductsStart = () => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_START,
  };
};

export const fetchProductsSuccess = (products, pageNumber) => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_SUCCESS,
    products,
    pageNumber: pageNumber
    
  };
};

export const fetchProductsFail = (error) => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_FAIL,
    error: error,
  };
};


export const asyncFetchProductsStart = (pageNumber, direction) => {
  return (dispatch) => {
    dispatch(fetchProductsStart());

    // const queryParams =
    //   '?auth=' + token + '&orderBy="userId"&equalTo="' + userId + '"'; ////sujeito a mudanças...



    let page = pageNumber || 1;

   

    if (direction === 'next') {
      page++;
    }


    if (direction === 'previous') {
      page --;
    }


    axiosOrder(
        {
            method: 'GET',
            url: `/shop/?page=${page}`
        }
    )
      .then((res) => {
        const fetchedProducts = [];
        for (let key in res.data) {
          fetchedProducts.push({
            ...res.data[key],
            id: key,
            value: res.data[key]
          });
        }
        console.log(fetchedProducts);
        dispatch(fetchProductsSuccess(fetchedProducts, page));
      })
      .catch((error) => {
        if (error.response) {
          console.log(error.toJSON())
            console.log(error.response.data);
            console.log(error.response.status);
            console.log(error.response.headers);
        }
        dispatch(fetchProductsFail(error));
      });
  };
};
