import './App.css';

import React, { useEffect, Suspense } from 'react';

import { BrowserRouter, Navigate, Routes, Route } from 'react-router-dom';

import { connect } from 'react-redux'; ///must-have

import Logout from '../src/pages/Auth/Logout/Logout';

import LandingPage from '../src/pages/Landing-Page/Landing-Page';

import Shop from '../src/pages/Shop/Shop';

import Auth from '../src/pages/Auth/Auth';

import Orders from '../src/pages/Orders/Orders';

import Checkout from './pages/Checkout/Checkout';

import Layout from './hoc/Layout/Layout'

import * as actionTypes from '../src/store/actions/index';

import Spinner from './components/UI/Spinner/Spinner';

function App(props) {
  useEffect((props) => {
    props.onTryAutoSignIn();
  }, []);

  let routes = (
    <Routes>
      <Route
        path="/auth"
        render={() => {
          return <Auth />;
        }}
      />
      <Route path="/shop" component={Shop} />
      <Route path="/" exact component={LandingPage} />
      <Navigate to="/" />
    </Routes>
  );

  if (props.token) {
    routes = (
      <Routes>
        <Route />
        <Route
          path="/checkout"
          render={(props) => {
            return <Checkout {...props} />;
          }}
        />
        <Route
          path="/orders"
          render={(props) => {
            return <Orders {...props} />;
          }}
        />
        <Route
          path="/auth"
          render={(props) => {
            return <Auth {...props} />;
          }}
        />
        <Route path="/logout" component={Logout} />
        <Route path="/shop" component={Shop} />
        <Route path="/" exact component={LandingPage} />
        <Navigate to="/" />
      </Routes>
    );
  }

  return (
    <BrowserRouter>
      <div>
        <Layout>
          <Suspense fallback={Spinner}>{routes}</Suspense>
        </Layout>
      </div>
    </BrowserRouter>
  );
}

const mapStateToProps = (state) => {
  return {
    token: state.auth.token !== null,
  };
};

const mapDispatchToProps = (state) => {
  return {
    onTryAutoSignIn: (token) =>
      dispatchEvent(actionTypes.checkAuthState(token)),
  };
};

// export default connect(mapStateToProps, mapDispatchToProps)(App); ///deprecado?

export default connect(mapStateToProps, mapDispatchToProps)(App);
