import React, { useEffect } from 'react';
import ProductItem from '../ProductItem/ProductItem';
import Spinner from '../UI/Spinner/Spinner';

import * as shopActionTypes from '../../store/actions/shop';

import './ProductList.css';
import { connect } from 'react-redux';

const ProductList = (props) => {
  useEffect(() => {
    props.onFetchProducts();
  }, []);


  let productList = <Spinner/>

  if (!props.loading) {
      productList = Object.entries(props.products).map(
        (product) => {
          console.log(product);
          return (
            <ProductItem title={product.title} price={product.price} description={product.description} imageUrl={product.imageUrl}></ProductItem>
          )
        }
      )
  }



  return (
    <div className="product__list">
        {productList}
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchProducts: () => {
      dispatch(shopActionTypes.asyncFetchProductsStart());
    },
  };
};

const mapStateToProps = (state) => {
  return {
    products: state.shop.products,
    loading: state.shop.loading
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
