import React from 'react';
import NavigationItem from '../NavigationItems/NavigationItem/NavigationItem';

import { NavLink } from 'react-router-dom';
import './NavigationItems.css';

const navItems = [
  { id: 'add-product', text: 'Add Product', link: '/add-product' },
  {
    id: 'admin-products',
    text: 'Admin Products',
    link: '/admin-products',
  },
  { id: 'cart', text: 'Cart', link: '/cart' },
  { id: 'orders', text: 'Orders', link: '/orders' },
];

const navigationItems = (props) => {
  let authenticate = (
    <ul
      className={
        props.type === 'main'
          ? 'main-header__item-list'
          : 'mobile-nav__item-list'
      }
    >
      <li
        className={
          (props.type === 'main' ? 'main-header__item' : 'mobile-nav__item') +
          ' auth'
        }
      >
        <NavLink to="/auth" exact>
          Get Started
        </NavLink>
      </li>
    </ul>
  );

  if (props.logout) {
    authenticate = (
      <ul
        className={
          props.type === 'main'
            ? 'main-header__item-list'
            : 'mobile-nav__item-list'
        }
      >
        <li
          className={
            (props.type === 'main' ? 'main-header__item' : 'mobile-nav__item') +
            ' auth'
          }
        >
          <NavLink to="/auth" exact>
            Logout
          </NavLink>
        </li>
      </ul>
    );
  }

  return (
    <React.Fragment>
      <ul
        className={
          props.type === 'main'
            ? 'main-header__item-list'
            : 'mobile-nav__item-list'
        }
      >
        <NavigationItem link={'/'} text="Shop" type={props.type}>
          Shop
        </NavigationItem>
        {props.logout &&
          navItems.map((item) => {
            // return (
            //   <NavigationItem
            //     key={item.id}
            //     link={item.link}
            //     type={props.type}
            //     text={item.text}
            //   ></NavigationItem>
            // );
          })}
        <NavigationItem link={'/shop'} text="Products" type={props.type}>
          Products
        </NavigationItem>
      </ul>
      {authenticate}
    </React.Fragment>
  );
};

export default navigationItems;
