import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';

import * as shopActionTypes from '../../store/actions/shop';

import './Pagination.css';

const Pagination = (props) => {
  //   0: {0: {…}, 1: {…}, 2: {…}, id: 'products', value: Array(3)}
  // 1: {id: 'currentPage', value: 1}
  // 2: {id: 'hasNextPage', value: true}
  // 3: {id: 'hasPreviousPage', value: false}
  // 4: {id: 'previousPageNumber', value: 0}
  // 5: {id: 'lastPage', value: 3}




  const pageChangedHandler = (pageNumber) => {


    console.log('clicked', pageNumber)
    console.log(props.products, props.currentPage);
    props.onPageChange(pageNumber);


    props.onFetchProducts(pageNumber);

  }

  let pagination = null;


  console.log(props.currentPage);

  if (!props.loading && props.products.length !== 0) {

    pagination = (
      <section className="pagination">
        {props.currentPage >= 1 && (<button type="button" className={props.currentPage === 1 ? 'active' : ''} onClick={() => {pageChangedHandler(1)}}>1</button>)}

        {/* {props.products[3].value && (
          <button type="button" onClick={() => {pageChangedHandler(props.products[5].value)}}>{props.products[1].value - 1}</button>
        )} */}

        {props.currentPage !== 1 &&
          props.currentPage !== props.products[5].value && (
            <button type="button" onClick={() => {pageChangedHandler(2)}}>{props.products[1].value}</button>
          )}



        {props.products[2].value &&
          props.products[1].value !== props.products[5].value && (
            <button type="button" onClick={() => {pageChangedHandler(props.products[1].value + 1)}}>{props.products[1].value + 1}</button>
          )}

        {props.products[1].value !== props.products[5].value && (
          <button type="button" onClick={()=> {pageChangedHandler(props.products[5].value)}}>{props.products[5].value}</button>
        )}
      </section>
    );
  }

  return pagination;
};

const mapStateToProps = (state) => {
  return {
    currentPage: state.shop.currentPage,
    products: state.shop.products,
    loading: state.shop.loading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchProducts: (pageNumber) => {
      dispatch(shopActionTypes.asyncFetchProductsStart(pageNumber))
    },
    onPageChange: (pageNumber) => {
      dispatch(shopActionTypes.pageChange(pageNumber));
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Pagination);
