import React, { useEffect, useState } from 'react';

import { connect } from 'react-redux';

// import { Elements } from '@stripe/react-stripe-js';
// import { loadStripe } from '@stripe/stripe-js';

import CartItem from '../../components/Cart/CartItemList/CartItem/CartItem';
import CartItemList from '../../components/Cart/CartItemList/CartItemList';
// import NoProduct from '../../components/Cart/NoProduct/NoProduct';
// import Spinner from '../../components/UI/Spinner/Spinner';
// import Wrapper from '../../components/Wrapper/Wrapper';






import * as cartActionTypes from '../../store/actions/cart';

import Cart from '../../components/Cart/Cart';

import '../Cart/Cart.css';

import './Checkout.css';

import axiosOrder from '../../axios-orders';
import CheckoutForm from '../../components/CheckoutForm/CheckoutForm';




const Checkout = (props) => {
  const [currentUser, setCurrentUser] = useState(); //undefined initially

  useEffect(() => {
    setCurrentUser(props.token);
    if (currentUser) {
      console.log('ENTERED');
      props.onFetchCart(props.token, props.userId);
    }
  }, [currentUser]);

  //   let cart = <Spinner></Spinner>;

  //   if (!props.loading && props.cart.products.length === 0) {
  //     cart = (
  //       <Wrapper>
  //         <CartItemList>
  //           <NoProduct>
  //             <li className="no-product cart-item">
  //               <h2>There are no products in your Cart.</h2>
  //             </li>
  //           </NoProduct>
  //         </CartItemList>
  //       </Wrapper>
  //     );
  //   }

  const submitOrderHandler = (order) => {
    axiosOrder.post(
      '/create-order',

      {
        order: order,
      },

      {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${props.token}`,
          UserId: `${props.userId}`,
        },
      }
    );
  };

  let cart;

  if (!props.loading && props.cart.products.length >= 1) {
    cart = (
      <React.Fragment>
        <CartItemList>
          {props.cart.products.map((product) => {
            return (
              <CartItem
                key={product.productId._id}
                productId={product.productId}
                quantity={product.quantity}
                isCheckout
              ></CartItem>
            );
          })}
        </CartItemList>
        <div className="cart-results">
          <h2>Total:</h2>
          <h2>US$ {props.cart.totalPrice.toFixed(2)}</h2>
          {/* <Elements stripe={stripePromise}>
            <CheckoutForm amount={props.totalPrice}></CheckoutForm>
          </Elements> */}
          {/* <form action="http://localhost:8080/create-order" method="POST"> */}
          {/* <div className="stripe-button-el">
            <button
              type="submit"
              onClick={() => {
                submitOrderHandler(props.cart);
              }}
            >
              <span>Pay with Card</span>
            </button>
          </div> */}

          <CheckoutForm></CheckoutForm>




          {/* </form> */}
        </div>
      </React.Fragment>
    );
  }

  return <Cart>{cart}</Cart>;
};

const mapStateToProps = (state) => {
  return {
    cart: state.cart,
    loading: state.cart.loading,
    totalPrice: state.cart.totalPrice,
    error: state.cart.error,
    token: state.auth.token,
    userId: state.auth.userId,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchCart: (token, userId) => {
      dispatch(cartActionTypes.asyncFetchCartStart(token, userId));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Checkout);
