import * as shopActionTypes from '../actions/shopActionTypes';

import axiosOrder from '../../axios-orders';

export const fetchProductsStart = () => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_START,
  };
};

export const fetchProductsSuccess = (products) => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_SUCCESS,
    products,
  };
};

export const fetchProductsFail = (error) => {
  return {
    type: shopActionTypes.FETCH_PRODUCTS_FAIL,
    error: error,
  };
};

export const asyncFetchProductsStart = (token, userId) => {
  return (dispatch) => {
    dispatch(fetchProductsStart);

    // const queryParams =
    //   '?auth=' + token + '&orderBy="userId"&equalTo="' + userId + '"'; ////sujeito a mudanças...

    axiosOrder
      .get('/shop') ///sujeito a mudanças, firebase
      .then((res) => {
        const fetchedProducts = [];
        for (let key in res.data) {
            console.log(key);
          fetchedProducts.push({
            ...res.data[key],
            id: key,
          });
        }
        dispatch(fetchProductsSuccess(fetchedProducts));
      })
      .catch((error) => {
        dispatch(fetchProductsFail(error));
      });
  };
};
